import { HttpClient, HttpResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class SimpleCalculatorService {
  constructor(private http: HttpClient) {
  }

  add(start: number, amount: number) {
    let r = this.http.get<number>(`http://localhost:5000/api/SimpleCalculator/Add?start=${start}&amount=${amount}`)
      .subscribe((response) => { return response; },
                 (error) => { console.log(error); });

    console.log(r);

//    this.http.get()
//      .subscribe(response => {
//        console.log(response.json());
//      });
    return start + amount;
  }

  subtract(start: number, amount: number) {
    return start - amount;
  }
}
