import { Component, OnInit } from '@angular/core';
import { SimpleCalculatorService } from '../simple-calculator.service';

@Component({
  selector: 'simple-calculator',
  templateUrl: './simple-calculator.component.html',
  styleUrls: ['./simple-calculator.component.css']
})
export class SimpleCalculatorComponent {
  start: number = 0;
  amount: number = 0;
  result: number = 0;
  isOrange: boolean = true; 
  
  constructor(public arithService: SimpleCalculatorService) {
  }

  onAdd() {
    this.arithService.add(this.start, this.amount).
      subscribe(response  => {
        this.result = response.valueOf();
      });
  }

  onSubtract() {
    this.arithService.subtract(this.start, this.amount).
      subscribe(response => {
        this.result = response.valueOf();
      });
  }

  onBgColourOrange() {
    this.isOrange= true; 
  }

  onBgColourSalmon() {
    this.isOrange = false; 
  }
}
<!DOCTYPE html>
<html>
<head>
  <link rel="stylesheet" href="simple-calculator.component.css">
</head>
<body>
  <div style="padding: 10px; text-align: center">
    <h1>Simple Calculator</h1>
    <br />
    <br />
    <div [style.backgroundColor]="isOrange ? 'orange' : 'salmon'" style="padding: 10px; color: blue; text-align: center; margin-left: 25%; margin-right: 25%; margin-bottom: 10px;">
      <label style="margin-right:5px" for="start">Start: </label>
      <input id="start" type="number" [(ngModel)]="start" />
      <br />
      <br />
      <label style="margin-right:5px" for="amount">Amount: </label>
      <input id="amount" type="number" [(ngModel)]="amount" />
      <br />
      <br />
      <button style="margin:10px" class="btn btn-primary" (click)="onAdd()">Add</button>
      <button style="margin:10px" class="btn btn-primary" (click)="onSubtract()">Subtract</button>
      <br />
      <br />
      <p id="result">Result: {{ result }}</p>
    </div>
    <b>
      <label style="padding-right: 10px; margin-left: 25%;" for="orange">Orange Background</label>
      <input type="radio" id="orange" name="bgcolour" value="Orange" (click)="onBgColourOrange()" checked="checked">
      <br />
      <label style="padding-right: 10px; margin-left: 25%;" for="salmon">Salmon Background</label>
    </b>
    <input type="radio" id="salmon" name="bgcolour" value="Salmon" (click)="onBgColourSalmon()">
  </div>
  <br />
  <footer style="padding-top: 25px; margin-left: 25%"><h6><i>Author: Chris Knowles - July 2023</i></h6></footer>
</body>
</html>
