import { Component, OnInit } from '@angular/core';
import { SimpleCalculatorService } from '../simple-calculator.service';

@Component({
  selector: 'simple-calculator',
  templateUrl: './simple-calculator.component.html',
  styleUrls: ['./simple-calculator.component.css']
})
export class SimpleCalculatorComponent implements OnInit {

  constructor(private service: SimpleCalculatorService) {
  }

  onAdd() {
    console.log("Add: ");
  }

  onSubtract() {
    console.log("Subtract: ");
  }

  ngOnInit() {
  }
}
<!DOCTYPE html>
<html>
  <head>
    <link rel="stylesheet" href="simple-calculator.component.css">
  </head>
  <body>
    <p>10 + 5 = {{ arithService.add(10, 5) }}</p>
    <p>10 - 5 = {{ arithService.subtract(10, 5) }}</p>
    <input id="start" type="number" />
    <input id="amount" type="number" />
    <button class="btn btn-primary" (click)="onAdd()">Add</button>
    <button class="btn btn-primary" (click)="onSubtract()">Subtract</button>
  </body>
</html>
